.random(1, 2 + ( ply:Botanism()) + ( ply:BotanismBuff())) == 2 ) then -- GET DIRTYnet.Start( "nAddChat" ); net.WriteVector( Vector( 200, 93, 0 ) ); net.WriteString( "[INCIDENT]: Ouch, I've cut myself."  ); net.Send( ply );	local dmg = DamageInfo(); dmg:SetAttacker( ply ); dmg:SetDamage( 25 ); dmg:SetDamageType( DMG_SLASH ); dmg:SetInflictor( ply ); ply:TakeDamageInfo( dmg );ply:EmitSound( "buttons/combine_button2.wav", 100, math.random( 90, 110 ) ); end										if ( math.random(1, 2 + ( ply:Botanism()) + ( ply:BotanismBuff())) == 2 ) then -- BURN FOODnet.Start( "nAddChat" ); net.WriteVector( Vector( 200, 93, 0 ) ); net.WriteString( "[INCIDENT]: Shit, I've harvested it wrong and wasted resources."  ); net.Send( ply );		self:Remove();	ply:GiveItem( "steelgrowseed" );ply:EmitSound( "buttons/combine_button2.wav", 100, math.random( 90, 110 ) ); return end							ply:GiveItem( "steelgrowseed" );				ply:GiveItem( "steelgrow" );			self:Remove();	end	endfunction ENT:CanPhysgun()		return false;	end	function nCombineRationTooHeavy( len )				GAMEMODE:AddChat( Color( 200, 0, 0, 255 ), "CombineControl.ChatNormal", "That's too heavy for you to carry.", { CB_ALL, CB_IC } );			end	net.Receive( "nCombineRationTooHeavy", nCombineRationTooHeavy );